mapper.select:

	select *
	from (
		select rownum rn, ano, storename, address, subtitle, mainimage
		from alley_board
		where rownum <= #{pageNum} * #{amount} order by ano asc
	)
	where rn > (#{pageNum}-1) * #{amount}

	
